{{ config(
            materialized='table',
                post_hook={
                    "sql": "create or replace table sleepycat_db.maplemonk.sleepycat_db_traffic_funnel_fact_items as with sales as ( select order_date:: date as order_date, case when upper(channel) = \'WOOCOMMERCE\' then \'DIRECT\' else channel end as channel, count(distinct order_id) as total_orders, sum(quantity) as total_quantity, sum(selling_price) as total_selling_price from sleepycat_db.maplemonk.sleepycat_db_sales_consolidated where lower(marketplace) like \'woocommerce\' group by 1,2 order by 1 desc ), pf_map as ( with sessions_users as ( select TO_DATE(date, \'YYYYMMDD\')date, sessions, totalusers, SCREENPAGEVIEWS, checkouts, addtocarts, ENGAGEDSESSIONS, SESSIONSOURCEMEDIUM, replace(SPLIT(SESSIONSOURCEMEDIUM, \' / \')[0],\'\"\',\'\') AS source, replace(SPLIT(SESSIONSOURCEMEDIUM, \' / \')[1],\'\"\',\'\') AS medium from SLEEPYCAT_DB.MAPLEMONK.GA4_SC_GA4_Sessions_by_date_source ), GA_CHANNEL_MAPPING as ( select * from ( select *, row_number() over (partition by lower(concat(lower(ifnull(ga_source,\'\')),lower(ifnull(ga_medium,\'\')))) order by 1) rw from SLEEPYCAT_DB.MAPLEMONK.GA_CHANNEL_MAPPING ) where rw=1 and lower(concat(ifnull(ga_source,\'\'),ifnull(ga_medium,\'\'))) is not null ), joined_table as ( select pf.date, pf.sessions, pf.totalusers, pf.SCREENPAGEVIEWS, pf.ENGAGEDSESSIONS, pf.checkouts, pf.addtocarts, pf.source, pf.medium, SESSIONSOURCEMEDIUM, ga_mapping.FINAL_channel as ga_mapped_channel from sessions_users as pf left join GA_CHANNEL_MAPPING as ga_mapping on lower(concat(ifnull(pf.SOURCE,\'\'),ifnull(pf.MEDIUM,\'\'))) = lower(concat(ifnull(GA_MAPPING.ga_source,\'\'),ifnull(GA_MAPPING.ga_medium,\'\'))) ) select date, upper(ga_mapped_channel) as ga_mapped_channel, sum(totalusers) as totalusers, sum(SCREENPAGEVIEWS) as SCREENPAGEVIEWS, sum(addtocarts) as addtocarts, sum(checkouts) as checkouts, sum(sessions) as sessions, sum(ENGAGEDSESSIONS)as ENGAGEDSESSIONS from joined_table group by 1,2 ), spend as ( select date ,upper(channel) marketing_Channel ,sum(ifnull(Clicks,0)) as Clicks ,sum(ifnull(Spend,0)) as Spend ,sum(ifnull(Impressions,0)) as Impressions ,sum(ifnull(Conversions,0)) as Conversions ,sum(ifnull(Conversion_Value,0)) as Conversion_Value from SLEEPYCAT_DB.MAPLEMONK.SLEEPYCAT_DB_PRODUCT_MARKETING_CONSOLIDATED where lower(channel) like any (\'%google%\',\'%facebook%\') group by 1,2 ) select coalesce(s_c.order_date,pf_map.date,sp.date) as order_date, upper(coalesce(s_c.channel,pf_map.ga_mapped_channel,sp.marketing_Channel)) as channel, s_c.total_orders, s_c.total_quantity, s_c.total_selling_price, pf_map.totalusers, pf_map.SCREENPAGEVIEWS, pf_map.sessions, pf_map.ENGAGEDSESSIONS, pf_map.checkouts, pf_map.addtocarts, Spend FROM sales s_c full outer JOIN pf_map on s_c.order_date = pf_map.date and lower(s_c.channel) = lower(pf_map.ga_mapped_channel) full outer join spend sp on (s_c.order_date) = sp.date and lower(s_c.channel) = lower(sp.marketing_Channel)",
                    "transaction": true
                }
            ) }}
            with sample_data as (

                select * from SLEEPYCAT_DB.information_schema.databases
            ),
            
            final as (
                select * from sample_data
            )
            select * from final
            