{{ config(
                        materialized='table',
                            post_hook={
                                "sql": "create or replace table snitch_db.maplemonk.availability_master as WITH PivotTable AS ( SELECT sku_group, SUM(CASE WHEN size_mapped = \'XS\' THEN units_on_hand ELSE 0 END) AS XS_units, SUM(CASE WHEN size_mapped = \'S\' THEN units_on_hand ELSE 0 END) AS S_units, SUM(CASE WHEN size_mapped = \'M\' THEN units_on_hand ELSE 0 END) AS M_units, SUM(CASE WHEN size_mapped = \'L\' THEN units_on_hand ELSE 0 END) AS L_units, SUM(CASE WHEN size_mapped = \'XL\' THEN units_on_hand ELSE 0 END) AS XL_units, SUM(CASE WHEN (size_mapped = \'XXL\' OR size_mapped = \'XXl\' or size_mapped = \'2XL\') THEN units_on_hand ELSE 0 END) AS XXL_units, SUM(CASE WHEN size_mapped = \'3XL\' THEN units_on_hand ELSE 0 END) AS XL3_units, SUM(CASE WHEN size_mapped = \'4XL\' THEN units_on_hand ELSE 0 END) AS XL4_units, SUM(CASE WHEN size_mapped = \'5XL\' THEN units_on_hand ELSE 0 END) AS XL5_units, SUM(CASE WHEN size_mapped = \'6XL\' THEN units_on_hand ELSE 0 END) AS XL6_units FROM SNITCH_DB.MAPLEMONK.INVENTORY_AGING_BUCKETS_SNITCH GROUP BY sku_group ) SELECT EOQ.SKU_GROUP, EOQ.product_name, EOQ.category, EOQ.final_ros, ROUND(SUM(EOQ.eoq_new),0) as total_eoq, SUM(EOQ.total_units_on_hand) as available_units, ROUND(SUM(EOQ.op),0) as order_point, CASE WHEN EOQ.final_ros >= 20 THEN \'1-Head\' WHEN EOQ.final_ros < 20 AND EOQ.final_ros >= 10 THEN \'2-Belly\' ELSE \'3-Tail\' END AS sku_class, PivotTable.XS_units, PivotTable.S_units, PivotTable.M_units, PivotTable.L_units, PivotTable.XL_units, PivotTable.XXL_units, PivotTable.XL3_units, PivotTable.XL4_units, PivotTable.XL5_units, PivotTable.XL6_units, (CASE WHEN PivotTable.XS_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.S_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.M_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.L_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.XL_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.XXL_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.XL3_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.XL4_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.XL5_units > 0 THEN 1 ELSE 0 END) + (CASE WHEN PivotTable.XL6_units > 0 THEN 1 ELSE 0 END) AS num_size_available FROM ( SELECT SKU_GROUP, product_name, category, final_ros, eoq_new, total_units_on_hand, op FROM snitch_db.maplemonk.eoq ) AS EOQ LEFT JOIN PivotTable ON EOQ.SKU_GROUP = PivotTable.sku_group GROUP BY sku_class, EOQ.sku_group, EOQ.product_name, EOQ.final_ros, EOQ.category, PivotTable.XS_units, PivotTable.S_units, PivotTable.M_units, PivotTable.L_units, PivotTable.XXL_units, PivotTable.XL_units, PivotTable.XL3_units, PivotTable.XL4_units, PivotTable.XL5_units, PivotTable.XL6_units ORDER BY sku_class, EOQ.final_ros DESC",
                                "transaction": true
                            }
                        ) }}
                        with sample_data as (

                            select * from snitch_db.information_schema.databases
                        ),
                        
                        final as (
                            select * from sample_data
