{{ config(
                        materialized='table',
                            post_hook={
                                "sql": "create or replace table eggozdb.maplemonk.all_bills as select bl.*, pps.* from ( SELECT tt.* FROM (SELECT rr.beat_number as original_beat_number, dba.beat_number as oper_beat_number, oo.delivery_date, oo.is_trial, oo.order_brand_type, rr.code AS Party_name, oo.order_price_amount AS Bill_amount, oo.name AS Bill_no, oo.bill_no as Manual_bill_no, oo.scheme_discount_amount, pi.invoice_due AS Pending, pi.invoice_status AS Paid_status, rr.area_classification as Area, bc.city_name, oo.status, oo.po_no, pi.id AS invoiceid, pi.modified_at as \"Date\", oo.distributor_id, rrp.name as Parent, oo.id as order_id, case when sp.name is null then sp2.name else sp.name end as Operator FROM eggozdb.maplemonk.my_sql_order_order oo LEFT JOIN eggozdb.maplemonk.my_sql_retailer_retailer rr ON rr.id = oo.retailer_id LEFT JOIN eggozdb.maplemonk.my_sql_distributionchain_beatassignment dba ON oo.beat_assignment_id = dba.id JOIN eggozdb.maplemonk.my_sql_base_city bc ON rr.city_id = bc.id LEFT JOIN eggozdb.maplemonk.my_sql_payment_invoice pi ON oo.id = pi.order_id LEFT JOIN eggozdb.maplemonk.my_sql_retailer_retailerparent rrp ON rr.parent_id = rrp.id LEFT JOIN (SELECT ddp.id, cau.name FROM eggozdb.maplemonk.my_sql_distributionchain_distributionpersonprofile ddp LEFT JOIN eggozdb.maplemonk.my_sql_custom_auth_user cau ON cau.id = ddp.user_id) sp ON sp.id = oo.distributor_id LEFT JOIN (SELECT ssp.id, cau.name FROM eggozdb.maplemonk.my_sql_saleschain_salespersonprofile ssp LEFT JOIN eggozdb.maplemonk.my_sql_custom_auth_user cau ON cau.id = ssp.user_id) sp2 ON sp2.id = oo.salesPerson_id) tt WHERE (tt.delivery_date BETWEEN \'2021-06-01 18:30:00 \' AND getdate()) AND tt.Paid_status <> \'Cancelled\' AND tt.is_trial <> TRUE AND tt.order_brand_type = \'branded\' ) bl left join ( SELECT sum(pp.pay_amount) as Instant_amount, pp.invoice_id FROM eggozdb.maplemonk.my_sql_payment_payment pp left join eggozdb.maplemonk.my_sql_payment_salestransaction ps on pp.salesTransaction_id = ps.id where ps.transaction_type <> \'Cancelled\' and ps.is_trial <> true and pp.pay_choice <> \'Credit Note\' group by pp.invoice_id ) pps on pps.invoice_id = bl.invoiceid ; create or replace table eggozdb.maplemonk.zero_billing_retailers as select zb.code, zb.area_classification, rrp.name AS parent_name, zb.beat_number, zb.onboarding_status, zb.onboarding_date, cast(timestampadd(minute,330,ooo.last_order_date) as date) as last_order_date, datediff(day,ooo.last_order_date,getdate()) as recency, ll.frequency, ll.total_bill_amount, ll.total_bill_amount/ll.frequency as average_order_amount from (select rr.id, rr.parent_id, rr.area_classification ,rr.code, rr.beat_number, rr.onboarding_status, cast(timestampadd(minute,330,rr.onboarding_date) as date) AS onboarding_date from eggozdb.maplemonk.my_sql_retailer_retailer rr left join (select * from eggozdb.maplemonk.my_sql_order_order where cast(timestampadd(minute,330,date) as date) between dateadd(day, -3, getdate()) and getdate()) o ON o.retailer_id = rr.id where o.retailer_id is null) zb left join eggozdb.maplemonk.my_sql_retailer_retailerparent rrp on zb.parent_id = rrp.id left join (select retailer_id, max(date) as last_order_date from eggozdb.maplemonk.my_sql_order_order where status<>\'cancelled\' and is_trial<>True group by retailer_id) ooo on ooo.retailer_id = zb.id left join (select retailer_id, count(orderId) as frequency, sum(order_price_amount) total_bill_amount from eggozdb.maplemonk.my_sql_order_order where status<>\'cancelled\' group by retailer_id) ll on ll.retailer_id = zb.id",
                                "transaction": true
                            }
                        ) }}
                        with sample_data as (

                            select * from EGGOZDB.information_schema.databases
                        ),
                        
                        final as (
                            select * from sample_data
                        )
                        select * from final
                        