                    "sql": "CREATE OR REPLACE TABLE snitch_db.maplemonk.Product_info as WITH master AS ( SELECT * FROM snitch_db.maplemonk.availability_master_v2 ), product_type as ( SELECT id, producttype FROM ( SELECT SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) AS id, producttype, ROW_NUMBER() OVER ( PARTITION BY SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) ORDER BY REPLACE(A.value:updated_at, \'\"\', \'\') DESC ) AS rw FROM snitch_db.maplemonk.metafields_products_graph_ql, LATERAL FLATTEN (input => metafields) A ) WHERE rw = 1 ), occassion AS ( SELECT SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) AS id, REPLACE(A.value:key, \'\"\', \'\') AS key_type, REPLACE(A.value:value, \'\"\', \'\') AS occassion FROM snitch_db.maplemonk.metafields_products_graph_ql, LATERAL FLATTEN (input => metafields) A WHERE key_type = \'occassion_new\' ), print_design AS ( SELECT SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) AS id, REPLACE(A.value:key, \'\"\', \'\') AS key_type, REPLACE(A.value:value, \'\"\', \'\') AS print_design FROM snitch_db.maplemonk.metafields_products_graph_ql, LATERAL FLATTEN (input => metafields) A WHERE key_type = \'print_design\' ), collar AS ( SELECT SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) AS id, REPLACE(A.value:key, \'\"\', \'\') AS key_type, REPLACE(A.value:value, \'\"\', \'\') AS collar FROM snitch_db.maplemonk.metafields_products_graph_ql, LATERAL FLATTEN (input => metafields) A WHERE key_type = \'collar\' ), material AS ( SELECT SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) AS id, REPLACE(A.value:key, \'\"\', \'\') AS key_type, REPLACE(A.value:value, \'\"\', \'\') AS material FROM snitch_db.maplemonk.metafields_products_graph_ql, LATERAL FLATTEN (input => metafields) A WHERE key_type = \'material\' ), sleeves_type AS ( SELECT SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) AS id, REPLACE(A.value:key, \'\"\', \'\') AS key_type, REPLACE(A.value:value, \'\"\', \'\') AS sleeves_type FROM snitch_db.maplemonk.metafields_products_graph_ql, LATERAL FLATTEN (input => metafields) A WHERE key_type = \'sleeves_type\' ), fit AS ( SELECT SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) AS id, REPLACE(A.value:key, \'\"\', \'\') AS key_type, REPLACE(A.value:value, \'\"\', \'\') AS fit FROM snitch_db.maplemonk.metafields_products_graph_ql, LATERAL FLATTEN (input => metafields) A WHERE key_type = \'fit\' ), color AS ( SELECT SUBSTRING(id, POSITION(\'Product/\' IN id) + LENGTH(\'Product/\')) AS id, REPLACE(REPLACE(REPLACE(A.value:values, \'[\', \'\'), \']\', \'\'), \'\"\', \'\') AS color FROM snitch_db.maplemonk.metafields_products_graph_ql, LATERAL FLATTEN (input => options) A WHERE REPLACE(A.value:position, \'\"\', \'\') = 1 ) SELECT master.*, product_type.producttype as product_type, occassion.occassion AS occassion, print_design.print_design AS print_design, collar.collar AS collar, material.material AS material, sleeves_type.sleeves_type AS sleeve_type, fit.fit AS fit, color.color AS color, case when lower(print_design) like \'%checks%\' then \'Checks\' when lower(print_design) like \'%animal%\' then \'Animal Print\' when lower(print_design) like \'%embroid%\' then \'Embroided\' when lower(print_design) like \'%floral%\' then \'Floral\' when lower(print_design) like \'%graphic%\' then \'Gr[ahic Print\' when lower(print_design) like \'%embelished%\' then \'Embelished\' when lower(print_design) like \'%plain%\' then \'Solid\' when lower(print_design) like \'%solid%\' then \'Solid\' when lower(print_design) like \'%printed%\' then \'Printed\' when lower(print_design) like \'%self%\' then \'Self Design\' when lower(print_design) like \'%stripe%\' then \'Stripes\' when lower(print_design) like \'%crochet%\' then \'Crochet\' when lower(print_design) like \'%polka%\' then \'Polka\' Else \'Others\' END as Designs FROM master LEFT JOIN product_type ON master.id = product_type.id LEFT JOIN occassion ON master.id = occassion.id LEFT JOIN print_design ON master.id = print_design.id LEFT JOIN collar ON master.id = collar.id LEFT JOIN material ON master.id = material.id LEFT JOIN sleeves_type ON master.id = sleeves_type.id LEFT JOIN fit ON master.id = fit.id LEFT JOIN color ON master.id = color.id",
