{{ config(
                        materialized='table',
                            post_hook={
                                "sql": "create or replace table eggozdb.maplemonk.area_classification_target as select mm.Date, mm.Area, mm.Region, mm.MTD_Sales, mm.Revenue_target, mm.MTD_Eggs_Sold, mm.no_of_days from (select sku.date, sku.area_classification as area, bgt.region, sku.mtd_net_sales as mtd_sales ,sku2.mtd_eggs_sold ,bgt.MTD_TARGET_JULY::FLOAT as revenue_target ,datediff(day,DATE_TRUNC(\'month\', current_date()), sku.date)+1 no_of_days, row_number() over (partition by sku.date, sku.area_classification order by sku.date, sku.area_classification) rownumber from maplemonk.sales_summary sku join maplemonk.target_region_wise_target_plan bgt on lower(bgt.area_classification) = lower(sku.area_classification) join maplemonk.eggs_sold_summary sku2 on lower(sku.area_classification) = lower(sku2.area_classification) and sku.date = sku2.date WHERE sku.date >= TO_DATE(DATE_TRUNC(\'month\', current_date())) AND sku.date <= getdate() ) mm where mm.rownumber = 1 ; create or replace table eggozdb.maplemonk.parent_retailer_target as SELECT sku.date, bgt.parent as parent_retailer_name, bgt.classification as area, sum(sku.net_sales) AS mtd_sales, sum(sku.eggs_sold) as mtd_eggs_sold, replace(bgt.\"Target-June\",\',\',\'\')::FLOAT as revenue_target ,datediff(day,\'2022-06-01\', sku.date)+1 no_of_days FROM eggozdb.maplemonk.target_mt_target bgt left join (select * from eggozdb.maplemonk.summary_reporting_table_beat_retailer_sku where date >= TO_DATE(\'2022-06-01\') and date <= \'2022-06-30\' )sku on lower(bgt.Parent) = lower(sku.parent_retailer_name) and lower(bgt.classification) = lower(sku.area) GROUP BY bgt.parent, bgt.classification, replace(bgt.\"Target-June\",\',\',\'\')::FLOAT, sku.date ; create or replace table eggozdb.maplemonk.beat_jse_target as SELECT sku.beat_number_original, bgt.\"JSE\", (replace(bgt.Revenue_target,\',\',\'\')::FLOAT/30)*datediff(day,DATE_TRUNC(\'month\', current_date()),getdate()) as mtd_target, sum(sku.net_sales) AS mtd_actuals, sum(sku.net_sales)/sum(sku.eggs_sold) as average_egg_price, sum(sku.eggs_sold) as eggs_sold, sum(sku.net_sales)/datediff(day,DATE_TRUNC(\'month\', current_date()),getdate()) as daily_runrate, (sum(sku.net_sales)/datediff(day,DATE_TRUNC(\'month\', current_date()),getdate())) * 30 as current_trajectory_forecast, replace(bgt.Revenue_target,\',\',\'\')::FLOAT as revenue_target, -1*(replace(bgt.Revenue_target,\',\',\'\')::FLOAT - (sum(sku.net_sales)/datediff(day,DATE_TRUNC(\'month\', current_date()),getdate()) * 30)) as shortfall_projected, ((replace(bgt.Revenue_target,\',\',\'\')::FLOAT - (sum(sku.net_sales)/datediff(day,DATE_TRUNC(\'month\', current_date()),getdate()) * 30))/(replace(bgt.Revenue_target,\',\',\'\')::FLOAT))*-1 as shortfall_percent, (replace(bgt.Revenue_target,\',\',\'\')::FLOAT-sum(sku.net_sales))/(30-datediff(day,DATE_TRUNC(\'month\', current_date()),getdate())) as required_run_rate, ((replace(bgt.Revenue_target,\',\',\'\')::FLOAT-sum(sku.net_sales))/(30-datediff(day,DATE_TRUNC(\'month\', current_date()),getdate())))/(sum(sku.net_sales)/sum(sku.eggs_sold)) as required_des_runrate, sum(sku.eggs_sold)/datediff(day,DATE_TRUNC(\'month\', current_date()),getdate()) as Current_DES, (((replace(bgt.Revenue_target,\',\',\'\')::FLOAT-sum(sku.net_sales))/(30-datediff(day,DATE_TRUNC(\'month\', current_date()),getdate())))/(sum(sku.net_sales)/sum(sku.eggs_sold)))-(sum(sku.eggs_sold)/datediff(day,DATE_TRUNC(\'month\', current_date()),getdate())) AS DES_increase FROM maplemonk.summary_reporting_table_beat_retailer_sku sku join maplemonk.target_jse_gt bgt on lower(bgt.beat_number_original) = lower(sku.beat_number_original) WHERE sku.date >= TO_DATE(DATE_TRUNC(\'month\', current_date())) AND sku.date <= getdate() GROUP BY sku.beat_number_original, bgt.JSE, replace(bgt.Revenue_target,\',\',\'\')::FLOAT ;",
                                "transaction": true
                            }
                        ) }}
                        with sample_data as (

                            select * from EGGOZDB.information_schema.databases
                        ),
                        
                        final as (
                            select * from sample_data
                        )
                        select * from final
                        